	TextureAccessCallPtr makeTextureSize1DFRgba32( type::TypesCache & cache
		, ExprPtr image
		, ExprPtr lod )
	{
		assert( lod->getType()->getKind() == type::Kind::eInt );
		return makeTextureAccessCall( cache.getBasicType( type::Kind::eInt )
			, TextureAccess::eTextureSize1DF
			, std::move( image )
			, std::move( lod ) );
	}

